import React from 'react';
import { Form, Segment, Button, Popup } from 'semantic-ui-react'
import styled from 'styled-components';

/*
const sliderThumbStyles = (props) => (`
  width: 25px;
  height: 25px;
  background: ${props.color};
  cursor: pointer;
  outline: 5px solid #333;
  opacity: ${props.opacity};
  -webkit-transition: .2s;
  transition: opacity .2s;
`);

const Styles = styled.div`
  display: flex;
  color: #888;
  margin-top: 2rem;
  margin-bottom: 2rem;
  .value {
    flex: 1;
    font-size: 2rem;
  }
  .slider {
    flex: 6;
    -webkit-appearance: none;
    width: 50%;
    height: 10px;
    border-radius: 5px;
    background: #efefef;  
    outline: none;
    &::-webkit-slider-thumb {
      -webkit-appearance: none;
      appearance: none;
      ${props => sliderThumbStyles(props)}
    }
    &::-moz-range-thumb {
      ${props => sliderThumbStyles(props)}
    }
  }
`;*/

const sliderThumbStyles = (props) => (`
  width: 20px;
  height: 20px;
  background: rgb(81, 146, 253);
  border-radius: 50px;
  cursor: pointer;
  -webkit-transition: .2s;
  transition: opacity .2s;
`);

const Styles = styled.div`
  display: flex;
  align-items: center;
  color: #888;
  .value {
    flex: 1;
    font-size: 2rem;
  }
  .slider {
    flex: 6;
    -webkit-appearance: none;
    width: 75%; 
    margin-right: 15px;
    height: 15px;
    border-radius: 10px;
    background: #efefef;
    outline: none;
    &::-webkit-slider-thumb {
      -webkit-appearance: none;
      appearance: none;
      ${props => sliderThumbStyles(props)}
    }
    &::-moz-range-thumb {
      ${props => sliderThumbStyles(props)}
    }
  }
`;

export default class Slider2 extends React.Component {

    state = {
        risk_setting: ''
    };

    handleOnChange = (e) => this.setState({ risk_setting: e.target.value });

    onSubmit = this._onSubmit.bind(this);

    render() {
        return (
          <Segment>
            <div className="ui container">
              <Form onSubmit={this.onSubmit}>
                <h3>Enter Risk Tolerance <Popup content="Please enter your risk aversion level on a scale of 0 to 10. 0: very risk averse; 10: very risk on." position="top center" trigger={<i class="info circle icon portfolio-popup"></i>} /></h3>
                <div className="inputLabel risk">0 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 10</div>
                <Styles opacity={this.state.risk_setting > 10 ? (this.state.risk_setting / 10) : .1} color={this.props.color}>
                  <input type="range" min={0} max={10} value={this.state.risk_setting} className="slider" onChange={this.handleOnChange}/>
                  <Button type='submit'>Submit</Button>
                </Styles>
              </Form>
            </div>
          </Segment>
        );
    }

    _onSubmit() {
        const payload = {
            risk_setting: parseFloat(this.state.risk_setting)
        };
        fetch('http://localhost:8080/risk_setting', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(payload)
        });
    }
}
